import java.util.Scanner;public class DayOfYear {	public static void main(String []args) {		Scanner keyboard;		keyboard = new Scanner(System.in);		int date, month, year;		int feb;		int dayth = 0;		System.out.print("Enter the date: ");		date = keyboard.nextInt();		System.out.print("Enter the month: ");		month = keyboard.nextInt();		System.out.print("Enter the year: ");		year = keyboard.nextInt();		if(year % 4 == 0 && year % 100 != 0) {			feb = 29;		}		else {			feb = 28;		}		switch (month) {			case 1:				dayth = date;				break;			case 2:				dayth = 31 + date;				break;			case 3: 				dayth = 31 + feb + date;				break;			case 4:				dayth = 62 + feb + date;				break;			case 5:				dayth = 92 + feb + date;				break;			case 6:				dayth =  123 + feb + date;				break;			case 7: 				dayth = 153 + feb + date;				break;			case 8:				dayth = 184 + feb + date;				break;			case 9: 				dayth = 215 + feb + date;				break;			case 10:				dayth = 245 + feb + date;				break;			case 11:				dayth = 276 + feb + date;				break;			case 12: 				dayth = 306 + feb + date;				break;		}		System.out.print("Day " + dayth + " of year");	}}